from typing import List
from collections import Counter, defaultdict

class Solution:
	def findSubstring(self, s: str, words: List[str]) -> List[int]:
		word_leg = len(words[0])
		word_count = Counter(words)
		rlt = []

		for i in range(word_leg):
			start = i
			# 起始窗口位子
			window = defaultdict(int)
			# 用來存出現過的字串
			words_used = 0
			# 用來記錄找到的字串數目

			for j in range(i, len(s) - word_leg + 1, word_leg):
				word = s[j:j + word_leg]

				if word not in word_count:
					# 如果現在這斷字串不在要找的目標中
					start = j + word_leg
					# 往下一段字串找並初始其他變數
					window = defaultdict(int)
					words_used = 0
					continue

				words_used += 1
				window[word] += 1

				while window[word] > word_count[word]:
					# 如果字串出現超過指定的數目
					window[s[start:start + word_leg]] -= 1
					# 將前面的字串拿掉一直到符合
					start += word_leg
					words_used -= 1

				if words_used == len(words):
					rlt.append(start)

		return rlt
    
solution = Solution()
solution.findSubstring("wordgoodgoodgoodbestword", ["word","good","best","good"])
